mapper.select:
select distinct indsMclsNm, indsMclsCd 
from gageinfo 
where indsLclsCd = #{indsLclsCd};

select distinct indsSclsNm, indsSclsCd 
from gageinfo 
where indsMclsCd = #{indsMclsCd};

select dong, lat, lng
from donglocation 
where lat between #{bx} and #{tx}
and lng between #{by} and #{ty}

select count(*)
from gageinfo  
where indsSclsNm = #{indsSclsNm}
and ldongNm = #{ldongNm}

select count(*)
from gageinfo  
where ldongNm = #{ldongNm}
